Method.Implementation:
if +amountOfPages = 0 { ;amountOfPages = "a" s page = ..GetPage( topic,amountOfPages, .sc ) Q:'sc s sc = page.Parse(amountOfPosts) Q:'sc sc } else{ ;amountOfPages - numeric s i = 0, numOfPages = amountOfPosts\25, numOfPosts = amountOfPosts#25 for { ;On one page maximum 25 posts s i = i + 1 if i > numOfPages Quit ;if there only one page with amount of posts < 25, then quit s page = ..GetPage( topic, i, .sc ) Q:'sc s sc = page.Parse(25) Q:'sc } if numOfPosts'=0 { s numOfPages = numOfPages + 1 s page = ..GetPage( topic,numOfPages, .sc ) Q:'sc s sc = page.Parse(numOfPosts) Q:'sc sc
} }
Q sc


	s html = ..Html 
	d html.Rewind()
	s ..pos=0, ..row = 1 

	s pos = html.FindAt( 1, ..#PostTableBegin ) 
    if pos = -1 {
	    s sc = $$$ERROR(,"Not found begin of post's table") Q 0
    }
    s pos = pos + $l(..#PostTableBegin)

    s end = html.FindAt( pos, ..#PostTableEnd ) 
    if end = -1 {
	    s sc = $$$ERROR(,"Not found end of post's table") Q 0
    }

	s sc = html.MoveTo( pos ) Q:'sc ""

	for num = 0:1:amountOfPosts {

		s pos = html.FindAt( pos, ..#PostBegin ) Q:pos=-1
		s sc = ..postBegin( pos ) Q:'sc
		s pos = pos + $l(..#PostBegin) ;offset

	}
