Method.Implementation:
s sc = 1
if ##class(Forum).NameExists( forum, .forumId ) {
#;s sc = ##class(Forum).NameDelete( forum ) Q:'sc sc #; 5803,"Forum.Topic" ERROR #5803: Can't get exclusive lock #; #; workaround: s sc = ..DeletePosts( forumId ) Q:'sc sc s sc = ..DeleteTopics( forumId ) Q:'sc sc
} else {
k data s data("Name") = forum w !, forum s sc = ##class(Forum).Insert( .data ) w " forum created! ", sc Q:'sc sc
}
w !,"Load Topics... " s sc = ..Scan( forum ) if 'sc w sc Q sc
w !,"Load Topic Posts ... " s sc = ..LoadPosts( forum ) if 'sc w sc Q sc
w !,"Update Authors... " s sc = ..UpdateAuthors( forum ) w sc
Q sc

s sc = 1, i = 1 s page = ..GetPage( forum, i, .sc ) Q:'sc sc s pages = page.Pages() Q:pages=0 $$$ERROR($$$GeneralError,"Can't parse pages count ")
while i <= pages {
s sc = page.Parse() Q:'sc w !, "Page #", i, " successfuly loaded", ! s i = i + 1, sc = page.Get( , i ) ;load next page #i in parser Q:'sc
} Q sc

s sql = "Select Distinct AuthorId From Forum.Post " _" Where Topic->Forum->Name = ? " s rs = ##class(%SQL.Statement).%ExecDirect(,sql, forum) s sc = 1, ok = 1
s geocoder = ..Geocoder()
while rs.%Next(){
k author s author = rs.AuthorId s sc = ##class(Forum.AuthorPage).Parse( .author ) ;get location s location = $g(author("Location")) if location = "" continue s point = ..Geolocation( location, geocoder, .sc ) if 'sc continue s aname = ##class(Forum.Author).NameGetStored( author ) w !, aname, ": ", location, " -> ", point s sc = ##class(Forum.Author).SetLocation( author, location, $p(point," "), $p(point," ",2) ) }
Q ok
