Export.CSP:
var todos = angular.module('todos', ['ui.bootstrap']);

todos.controller('TodoController', function($scope,$http) {
$scope.currentPage=1;
$scope.itemsPerPage=1;

$http.get('http://37.139.17.101:57772/csp/restforum/topicpage').success(function(result){
	$scope.items=(function(){
		var todos = [];
		for (var i=1;i<=result.topicCount;i++) {
			todos.push({ text:'page '+i, done:false});
		}
		return todos;
	})();
	$scope.totalItems=$scope.items.length;
});


});
